<script>
  import { modal } from '@stores/app'
  import { createEventDispatcher } from 'svelte'
  import { fade } from 'svelte/transition'
  import {Spinner} from '@components/misc'
  const dispatch = createEventDispatcher()

  Mousetrap.bind(['esc'], () => {
    modal.hide()
  })

  async function closeModal() {
    modal.hide()
  }

  $: header = $modal.header
  $: button = $modal.button
  $: variants = $modal.variants || 'max-w-md'

</script>

{#if $modal.visible}
  <div class="modal mousetrap" transition:fade={{ duration: 100 }}>
    <div class="modal-background" on:click={$modal.disableClose ? () => {} : closeModal}></div>
    <div class="modal-card {variants}">
      {#if header}
        <header class="modal-card-head">
          <p class="modal-card-title">
            {#if header.icon}
              <span class="icon">
                <i class={header.icon}></i>
              </span>
            {/if}
            <span class="modal-title">{header.title}</span>
          </p>
          <button id="close-modal" class="delete" aria-label="close" on:click={closeModal}></button>
        </header>
      {/if}
      <div class="modal-card-body">
        <svelte:component this={$modal.component} { ...$modal.componentProps } />
      </div>
      {#if button}
        <footer class="modal-card-foot">
          <button 
            id="modal-footer-button"
            class="button is-link" 
            disabled={false}
            on:click={button.onclick}>
              {#if button.loading}
                <Spinner size="lg" />
              {:else}
                {button.label}
              {/if}
          </button>
        </footer>
      {/if}
    </div>
  </div>
{/if}

