<script>

  import { slide, fade } from 'svelte/transition'
  import { createEventDispatcher } from 'svelte'
  const dispatch = createEventDispatcher()

  import {TextInput} from '@components/inputs'

  export let variants = ''

  export let link = {
    label : '',
    url : ''
  };

</script>

<form class="p-4 shadow {variants}" in:fade={{ duration: 200 }} on:submit|preventDefault={() => dispatch('submit', link)}>
  <TextInput bind:value={link.label} autofocus={true} prefix="Title" placeholder="About Me" variants="mb-2" />
  <TextInput bind:value={link.url} prefix="url" placeholder="/about-me" />
  <div class="flex justify-end mt-4">
    <button class="text-sm mr-4" type="button" on:click={() => dispatch('cancel')}>
      Cancel
    </button>
    <br>
    <button class="submit-button hover:bg-gray-200" type="submit">
      Submit
    </button>
  </div>
</form>

<style>
  .submit-button {
    @apply text-sm py-2 px-3 rounded bg-gray-100 text-gray-900 transition-colors duration-200;
  }
</style>