<script>
  import {createEventDispatcher, onMount, onDestroy} from 'svelte'
  const dispatch = createEventDispatcher()
  import _ from 'lodash'
  import {CodeMirror} from '@components'
  import {Tabs} from '@components/misc'
  import {CodePreview} from '@components/misc'
  import {SaveButton} from '@components/buttons'
  import { compileScss, processStyles, wrapInStyleTags, buildPageHTML, buildSiteHTML,buildPagePreview,  getComponentCSS } from 'utils'

  import {content,pageData,domainInfo,settings,site,tailwind,getCombinedTailwindConfig} from '@stores/data'
  import {modal} from '@stores/app'

  let { final:pageStyles } = $pageData.styles
  let { styles } = $site


  let pageHTML
  let siteHTML 
  let allComponentsCSS = getComponentCSS($content)
  let allPages = buildSiteHTML($site.pages)

  let loading = false

  async function compileStyles() {
    loading = true
    const result = await processStyles(
      unprocessedStyles, 
      '',
      {
        tailwindConfig, 
        includeBase: false,
        includeTailwind: false,
        purge: false
      }
    );
    if (!result.error) {
      processedStyles = result
      if (!gettingTailwind) {
        loading = false
      } 
    } 
  }

  let pagesWithSiteStyles = allPages;

  $: {
    pagesWithSiteStyles = allPages.map(page => ({
      html: page,
      css: processedStyles + pageStyles + allComponentsCSS
    }))
  }


  let unprocessedStyles = styles.raw;
  let processedStyles = styles.final; 
  let moduleTailwindConfig = styles.tailwind || styles.tailwindConfig;

  $: tailwindConfig = moduleTailwindConfig.replace('export default ','')
  $: combinedTailwindConfig = getCombinedTailwindConfig(tailwindConfig)

  let tailwindConfigChanged = false
  let gettingTailwind = false
  $: {
    if (tailwindConfigChanged) {
      loading = true
      gettingTailwind = true
      tailwind.swapInConfig(combinedTailwindConfig, () => {
        loading = false
        gettingTailwind = false 
      })
    }
  }

  const cachedTailwindConfig = moduleTailwindConfig.replace('export default ','')
  let shouldReloadTailwind = false

  $: {
    styles = {
      raw: unprocessedStyles,
      final: processedStyles,
      tailwind: tailwindConfig
    }
    if (tailwindConfig !== cachedTailwindConfig) {
      shouldReloadTailwind = true
    }
  }

  $: cssSize = (new Blob([processedStyles]).size / 1000).toFixed(1)

  const tabs = [
    {
      id: 'styles',
      label: 'CSS'
    },
    {
      id: 'tw',
      label: 'Tailwind Config'
    },
  ]

  let selectedTab = tabs[0]

</script>

<div class="h-full flex flex-col">
  <div class="flex flex-row flex-1">
    <div class="w-1/2 flex-1 flex flex-col">
      <Tabs {tabs} bind:activeTab={selectedTab} />
        {#if selectedTab.id === 'styles'}
          <CodeMirror 
            bind:value={unprocessedStyles} 
            on:change={_.debounce( () => { compileStyles() }, 1000 )}
            mode="css" 
          />
        {:else}
          <CodeMirror 
            prefix="export default "
            bind:value={moduleTailwindConfig} 
            on:change={_.debounce( () => { 
              tailwindConfigChanged = true
              compileStyles() 
            }, 1000 )}
            mode="javascript" 
            docs="https://tailwindcss.com/docs/configuration"
          />
        {/if} 
    </div>
    <div class="w-1/2 flex-1">
      <CodePreview 
        multiple={true}
        pages={pagesWithSiteStyles}
      />
    </div>
  </div>
  <div class="flex justify-end py-2">
    <SaveButton {loading} on:click={() => {
      site.saveStyles(styles)
      if (shouldReloadTailwind) {
        tailwind.saveSwappedInConfig()
      } else {
        tailwind.swapOutConfig()
      }
      modal.hide()
    }}>Save</SaveButton>
  </div>
</div>


