import { writable, readable, derived, get } from 'svelte/store';
import { site } from '@stores/data'
import {content} from './page'

let pageData
const store = writable({
  id: '',
  title: '',
  content: [],
  dependencies: {
    headEmbed: '',
    libraries: []
  },
  styles: {
    raw: '',
    final: '',
    tailwind: ''
  },
  fields: []
})

store.subscribe(s => {
  pageData = s
  if (s && site) {
    site.pages.modify(s)
  }
  if (s && content) {
    content.set(s.content)
  }
})

export default {
  save: (property, value) => {
    store.update(s => ({
      ...s, 
      [property]: value
    }))
  },
  saveStyles: (styles) => {
    store.update(s => ({
      ...s, 
      styles
    }))
  },
  subscribe: store.subscribe,
  set: store.set
}