<script>
  import {onMount} from 'svelte'
  import {buildPageHTML} from 'utils'

  export let page

  let preview = ''
  onMount(async () => {
    preview = await buildPageHTML(page.content)
  })

</script>

<div class="page-preview">
  <iframe class="" title="page preview" srcdoc={preview}></iframe>
</div>


<style>
  .page-preview {
    @apply relative;
    height: 15vw;
  }
  iframe {
    width: 50vw;
    transform: scale(0.205);
    position: absolute;
    transform-origin: left;
  }
</style>