<svelte:window on:resize={resizePreview} />

<script>
  import {onMount} from 'svelte'
  import {fade} from 'svelte/transition'
  import {buildPagePreview,wrapInStyleTags} from 'utils'
  import {site,tailwind,pageData} from '@stores/data'

  export let page

  let preview = wrapInStyleTags($tailwind) + wrapInStyleTags($site.styles.final) + wrapInStyleTags($pageData.styles.final) + buildPagePreview(page)

  let iframeLoaded = false

  let container
  let iframe
  let scale

  function resizePreview() {
    const {clientWidth:parentWidth} = container
    const {clientWidth:childWidth} = iframe
    scale = parentWidth / childWidth
  }

  onMount(resizePreview)
</script>

<div class="page-preview" bind:this={container}>
  <iframe bind:this={iframe} style="transform: scale({scale})" class:fadein={iframeLoaded} title="page preview" srcdoc={preview} on:load={() => {iframeLoaded = true }}></iframe>
</div>

<style>
  .page-preview {
    @apply relative overflow-hidden pointer-events-none;
    height: 15vh;
  }
  iframe {
    @apply opacity-0 transition-opacity duration-200;
    width: 100vw;
    transform-origin: top left;
    height: 1000vh;
  }
  .fadein {
    @apply opacity-100;
  }
</style>