import { writable, readable, derived, get } from 'svelte/store';
import {updatePage} from '@fb/firestore/domains'

const store = writable({
  headEmbed : '',
  libraries: [],
  // customScripts: [],
})

export default {
  save: (dependencies) => {
    if (dependencies) {
      store.set(dependencies)
      updatePage({ dependencies })
    }
  },
  set: (dependencies) => {
    if (dependencies) {
      store.set(dependencies)
    }
  },
  subscribe: store.subscribe
}
