import { writable, readable, derived, get } from 'svelte/store';
import {getAllSymbols} from '@fb/firestore/domains'
import domainInfo from './domainInfo'

const store = writable([])

export default {
  reload: async () => {
    const symbols = await getAllSymbols(get(domainInfo).domainName)
    store.set(symbols)
    return symbols
  },
  subscribe: store.subscribe,
  set: store.set
}